export  default class SelectBankScene extends BaseComponent {

    constructor(props) {
        super(props);


        let getSectionData = (dataBlob, sectionID) => {
            return dataBlob[sectionID];
        };

        let getRowData = (dataBlob, sectionID, rowID) => {
            return dataBlob[sectionID + ":" + rowID];
        };


        const dataSource =  new ListView.DataSource({
            getSectionData: getSectionData,
            getRowData: getRowData,
            rowHasChanged: (r1, r2) => r1 !== r2,
            sectionHeaderHasChanged: (s1, s2) => s1 !== s2,
        });

        this.state = {

            renderPlaceholderOnly: 'blank',
            dataSource:dataSource,

        };

        this.base_bankData = [];
        this.new_bankData=[{title:'A',data:[]},{title:'B',data:[]},{title:'C',data:[]},{title:'D',data:[]},{title:'E',data:[]},{title:'F',data:[]},{title:'G',data:[]},{title:'H',data:[]},{title:'I',data:[]},{title:'J',data:[]},
            {title:'K',data:[]},{title:'L',data:[]},{title:'M',data:[]},{title:'N',data:[]},{title:'O',data:[]},{title:'P',data:[]},{title:'Q',data:[]},{title:'R',data:[]},{title:'S',data:[]},{title:'T',data:[]},{title:'U',data:[]}
            ,{title:'V',data:[]},{title:'W',data:[]},{title:'X',data:[]},{title:'Y',data:[]},{title:'Z',data:[]}
        ];
        this.hotBankData = [];


    }


    initFinish = () => {
        this.setState({
            renderPlaceholderOnly: 'loading',
        });

        this.loadData();
    }

    loadData=()=>{
        request(Url.GET_BANK_LIST,'post',{bankName:'银行',rows:'1000'}).then((response) => {

            console.log(response);
             this.base_bankData = response.mjson.data.info_list;
             this.bankDataDispose();

            },
            (error) => {
                this.setState({
                    renderPlaceholderOnly: 'error',
                });
            });

    }


    bankDataDispose=()=>{

        for(bankItem of this.base_bankData){

            if(bankItem.isHot ==1 &&  bankItem.bankShortName){
                this.hotBankData.push(bankItem);
            }
            let  bankName = bankItem.bankName;
            // console.log('开始',bankName);
            // console.log(bankName.indexOf('中国',0));
            if(bankName.indexOf('中国',0)==0 && bankName.indexOf('中国银行',0)!==0){
                bankName = bankName.substring(2,bankName.length);
                // console.log(bankName);
            }
            let zimu = this.checkCh(bankName.charAt(0));
            // console.log('开始zimu',zimu);
            if(zimu.length>1){
                zimu = zimu.substring(0,1);
            }
            // console.log('bankName',bankName,'zimu',zimu);

            let index =  this.zimuIndex(zimu);
            this.new_bankData[index].data.push(bankItem);
        }
        this.new_bankData=this.new_bankData.filter(item=>item.data.length>0);
        this.setListData(this.new_bankData);

    }

    setListData = (array)=> {

        var dataBlob = {}, sectionIDs = [], rowIDs = [], cars = [], sectionTitleArray = ['热'];
        for (var i = 0; i < array.length; i++) {
            //把组号放入sectionIDs数组中
            sectionIDs.push(i);
            //把组中内容放入dataBlob对象中
            dataBlob[i] = array[i].title;
            sectionTitleArray.push(array[i].title);
            //把组中的每行数据的数组放入cars
            cars = array[i].data;
            //先确定rowIDs的第一维
            rowIDs[i] = [];
            //遍历cars数组,确定rowIDs的第二维
            for (var j = 0; j < cars.length; j++) {
                rowIDs[i].push(j);
                //把每一行中的内容放入dataBlob对象中
                dataBlob[i + ':' + j] = cars[j];
            }

        }

        this.setState({

            dataSource: this.state.dataSource.cloneWithRowsAndSections(dataBlob, sectionIDs, rowIDs),
            sectionTitleArray: sectionTitleArray,
            renderPlaceholderOnly: 'success',


        });
    };

    checkCh=(ch)=>{
        var uni = ch.charCodeAt(0);
        //如果不在汉字处理范围之内,返回原字符,也可以调用自己的处理函数
        if(uni > 40869 || uni < 19968)
            return ch; //dealWithOthers(ch);
        //检查是否是多音字,是按多音字处理,不是就直接在strChineseFirstPY字符串中找对应的首字母
        return (oMultiDiff[uni]?oMultiDiff[uni]:(strChineseFirstPY.charAt(uni-19968)));
    }

    zimuIndex=(ch)=>{
        switch(ch){
            case 'A':
                return 0;
                break;
            case 'B':
                return 1;
                break;
            case 'C':
                return 2;
                break;
            case 'D':
                return 3;
                break;
            case 'E':
                return 4;
                break;
            case 'F':
                return 5;
                break;
            case 'G':
                return 6;
                break;
            case 'H':
                return 7;
                break;
            case 'I':
                return 8;
                break;
            case 'J':
                return 9;
                break;
            case 'K':
                return 10;
                break;
            case 'L':
                return 11;
                break;
            case 'M':
                return 12;
                break;
            case 'N':
                return 13;
                break;
            case 'O':
                return 14;
                break;
            case 'P':
                return 15;
                break;
            case 'Q':
                return 16;
                break;
            case 'R':
                return 17;
                break;
            case 'S':
                return 18;
                break;
            case 'T':
                return 19;
                break;
            case 'U':
                return 20;
                break;
            case 'V':
                return 21;
                break;
            case 'W':
                return 22;
                break;
            case 'X':
                return 23;
                break;
            case 'Y':
                return 24;
                break;
            case 'Z':
                return 25;
                break;

        }
    }


    render() {
        if (this.state.renderPlaceholderOnly != 'success') {
            return (
                <View style={styles.root}>
                    {
                        this.loadView()
                    }
                    <NavigationView
                        title={'选择银行'}
                        backIconClick={this.backPage}
                    />
                </View>
            )
        }
        return (
            <View style={styles.root}>
                <StatusBar barStyle={'dark-content'}/>
                <View style={{backgroundColor :fontAnColor.COLORA3,height:Pixel.getPixel(50),paddingHorizontal:Pixel.getPixel(15),justifyContent:'center',
                    flexDirection:'row', alignItems:'center'
                }}>
                    <TouchableOpacity style={{height:Pixel.getPixel(30), width:width - Pixel.getPixel(30), backgroundColor:'white',justifyContent:'center',
                        flexDirection:'row',
                        alignItems:'center'
                    }} onPress={()=>{
                        this.toNextPage({
                            name:'SeekBankScene',
                            component:SeekBankScene,
                            params:{
                                getBankData:(rowData)=>{
                                    this.props.getBankData && this.props.getBankData(rowData);
                                }
                            }
                        })

                    }}>
                        <Image style={{width:Pixel.getPixel(20),height:Pixel.getPixel(20),marginHorizontal:Pixel.getPixel(10)}} source={require('../../../images/findIcon.png')}/>
                        <Text style={styles.textInput}>搜索银行名称</Text>
                    </TouchableOpacity>
                </View>
                {
                    this.state.dataSource && (
                        <ListView ref="listView"
                                  style={{flex:1}}
                                  removeClippedSubviews={false}
                                  dataSource={this.state.dataSource}
                                  renderRow={this.renderRow}
                                  renderSectionHeader={this.renderSectionHeader}
                                  renderHeader={this.renderHeader}
                                  pageSize={300}

                        />)
                }

                <ZNListIndexView indexTitleArray={this.state.sectionTitleArray} indexClick={this._indexAndScrollClick}/>
                <NavigationView
                    title={'选择银行'}
                    backIconClick={this.backPage}
                    wrapStyle={{backgroundColor:'white'}}
                    titleStyle={{color:fontAnColor.COLORA0}}

                />
            </View>
        );
    }

    _indexAndScrollClick = (index)=> {

        let listView = this.refs.listView;
        if(index<1){
            listView.scrollTo({x: 0, y: 0, animated: true});
            return;

        }

        let  headHeight = (this.new_bankData.length%4+1) * ((width-Pixel.getPixel(30))/4);
        let scrollY = (index-1) * Pixel.getPixel(40)+headHeight;
        for (let i = 0; i < index-1; i++) {
            let rowIndex =this.new_bankData[i].data.length;
            scrollY += (rowIndex * Pixel.getPixel(44));
        }
        listView.scrollTo({x: 0, y: scrollY, animated: true});

    };

    renderHeader=()=>{
        return(
            <View style={{
                backgroundColor: 'white',
                flexDirection: 'row',
                alignItems: 'center',
                flexWrap: 'wrap',
                width:width,
            }}>
                {
                    this.hotBankData.map((data,index)=>{
                        return(
                        <TouchableOpacity key={index} onPress={()=>{ this.props.getBankData && this.props.getBankData(data); this.backPage()}}>
                            <View style={{
                                backgroundColor: 'white',
                                alignItems: 'center',
                                justifyContent: 'center',
                                height: (width-Pixel.getPixel(30))/4,
                                width:(width-Pixel.getPixel(30))/4,
                                borderBottomWidth:StyleSheet.hairlineWidth,
                                borderBottomColor:fontAnColor.COLORA3,
                                borderRightWidth:StyleSheet.hairlineWidth,
                                borderRightColor:fontAnColor.COLORA3,
                            }} >
                                <Image  style={{marginBottom:Pixel.getPixel(15)}} source={this.getBankImage(data.bankShortName)}/>
                                <Text style={{ width:(width-Pixel.getPixel(33))/4 ,textAlign:'center',
                                    fontSize:fontAnColor.LITTLEFONT26,
                                    position: 'absolute',
                                    bottom:Pixel.getPixel(5),
                                    left:0,
                                    right:0,
                                    height:Pixel.getPixel(25)
                                }} numberOfLines={1}>{ (data.bankShortName.indexOf('中国',0)==0 && data.bankShortName.indexOf('中国银行',0)!==0)?data.bankShortName.substring(2,data.bankShortName.length) :data.bankShortName}</Text>
                            </View>
                        </TouchableOpacity>

                        )
                    })
                }
            </View>
        )

    }
    // 每一行中的数据
    renderRow = (rowData, sectionID, rowID) => {

        return (
            <TouchableOpacity onPress={() => {this.props.getBankData && this.props.getBankData(rowData); this.backPage()}}>
                <View style={styles.rowCell}>
                    <Text allowFontScaling={false}  style={styles.rowCellText}>{rowData.bankName}</Text>
                </View>
            </TouchableOpacity>
        )
    };

    // 每一组对应的数据
    renderSectionHeader = (sectionData, sectionId) => {

        return (
            <View style={styles.sectionHeader}>
                <Text allowFontScaling={false}  style={styles.sectionText}>{sectionData}</Text>
            </View>
        );
    }

    renderSeekRow=(rowData)=>{
        return (
            <TouchableOpacity onPress={() => {this.props.getBankData && this.props.getBankData(rowData); this.backPage()}}>
                <View style={styles.rowCell}>
                    <Text allowFontScaling={false}  style={styles.rowCellText}>{rowData.bankName}</Text>
                </View>
            </TouchableOpacity>
        )
    }

    getBankImage=(name)=>{

        if(name.indexOf('工商银行')>-1){
            return require('../../../images/mine/guangfa_account/gs.png');
        }else if(name.indexOf('中国银行')>-1){
            return require('../../../images/mine/guangfa_account/zh.png');
        }else if(name.indexOf('建设银行')>-1){
            return require('../../../images/mine/guangfa_account/js.png');
        }else if(name.indexOf('农业银行')>-1){
            return require('../../../images/mine/guangfa_account/ny.png');
        }else if(name.indexOf('交通银行')>-1){
            return require('../../../images/mine/guangfa_account/jt.png');
        }else if(name.indexOf('邮储银行')>-1){
            return require('../../../images/mine/guangfa_account/yz.png');
        }else if(name.indexOf('招商银行')>-1){
            return require('../../../images/mine/guangfa_account/zs.png');
        }else if(name.indexOf('平安银行')>-1){
            return require('../../../images/mine/guangfa_account/pa.png');
        }else if(name.indexOf('民生银行')>-1){
            return require('../../../images/mine/guangfa_account/ms.png');
        }else if(name.indexOf('光大银行')>-1){
            return require('../../../images/mine/guangfa_account/gd.png');
        }else if(name.indexOf('华夏银行')>-1){
            return require('../../../images/mine/guangfa_account/hx.png');
        }else if(name.indexOf('中信银行')>-1){
            return require('../../../images/mine/guangfa_account/zx.png');
        }else if(name.indexOf('浦发银行')>-1){
            return require('../../../images/mine/guangfa_account/pf.png');
        }else if(name.indexOf('广发银行')>-1){
            return require('../../../images/mine/guangfa_account/gf.png');
        }else if(name.indexOf('兴业银行')>-1){
            return require('../../../images/mine/guangfa_account/zx.png');
        }
        return require('../../../images/mine/guangfa_account/yinhang-da.png');

    }


}
class ZNListIndexView extends Component{

    render(){
        const {indexTitleArray}=this.props;
        return(
            <View style={styles.indexView}>
                {
                    indexTitleArray.map((data,index)=>{
                        return(
                            <TouchableOpacity key={index} style={styles.indexItem} onPress={()=>{

                                this.props.indexClick(index);

                            }}>
                                <Text allowFontScaling={false}  style={styles.indexItemText}>{data}</Text>
                            </TouchableOpacity>
                        )
                    })


                }
            </View>
        )
    }

}
